import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { filterCountriesByStatus, getCountries } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginated from "./Paginated";

const Home = () => {
  //todo                                           DEFINIMOS LOS ESTADOS Y EL PAGINADO
  const dispatch = useDispatch();
  const allCountries = useSelector((state) => state.countries);
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesPerPage, setCountriesPerPage] = useState(10);
  const indexOfLastCountry = currentPage * countriesPerPage;
  const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
  const currentCountries = allCountries.slice(
    indexOfFirstCountry,
    indexOfLastCountry
  );

  const paginated = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  //todo                                             DESPACHAMOS TODOS LOS PAISES
  useEffect(() => {
    dispatch(getCountries());
  }, []);

  const handleClick = (event) => {
    event.preventDefault();
    dispatch(getCountries());
  };

  const handleFilterStatus = (event) => {
    dispatch(filterCountriesByStatus(event.target.value));
  };

  //todo                                                     RENDERIZADO
  return (
    <div>
      <Link to="/character"></Link>
      <h1>COUNTRIES</h1>
      <button
        onClick={(event) => {
          handleClick(event);
        }}
      >
        "Volver a cargar todos los paises"(pasar a Ingles)
      </button>

      <div>
        <select>
          <option value="asc">Ascendente</option>
          <option value="desc">Descendente</option>
        </select>
        <select onChange={handleFilterStatus}>
          <option value="all">All</option>
          <option value="continent">Continent</option>
          <option value="activities">Activity</option>
        </select>
        <h2>hola</h2>
        <Paginated
          countriesPerPage={countriesPerPage}
          allCountries={allCountries.length}
          currentPaginated={paginated}
        />
        {currentCountries?.map((country) => {
          return (
            <fragment>
              <Card
                name={country.name}
                flags={country.flags}
                continent={country.continent}
              />
            </fragment>
          );
        })}
      </div>
    </div>
  );
};
export default Home;
