import React from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { getCountries, getDetailCountry } from "../../actions";
import "./Detail.css";
import NavBar from "../NavBar/NavBar";

const Detail = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const [detail, setDetail] = useState({});
  let myCountry = useSelector((state) => state.detail);

  useEffect(() => {
    try {
      dispatch(getDetailCountry(id));
      dispatch(getCountries());
      setDetail(myCountry);
    } catch (error) {
      console.log(error.message);
      alert("Algo salio mal");
    }
    return setDetail({});
  }, [dispatch, id]);

  console.log(myCountry);

  return (
    <div className="all">
      <NavBar />
      {detail ? (
        <div className="Countainer">
          <h1 className="Title">Country: {detail.name}</h1>
          <img className="Img" src={detail.flags} alt={detail.name} />
          <div>
            <h1 className="Title">Detail:</h1>
            <h2 className="SubTitle">ID: {detail.id}</h2>
            <h2 className="SubTitle">Capital: {detail.capital}</h2>
            <h2 className="SubTitle">Continent: {detail.continent}</h2>
            {detail.subregion && (
              <h2 className="SubTitle">Subregion: {detail.subregion}</h2>
            )}
            {detail.area && (
              <h2 className="SubTitle">Area: {detail.area} km2</h2>
            )}
            <h2 className="SubTitle">Population: {detail.population}</h2>
          </div>

          {detail.Activities?.length ? (
            <div>
              <Link className="SubTitle" to="/activities">
                <button className="ButtonLink3">Activities</button>
              </Link>
              {detail.Activities?.map((c) => {
                return (
                  <div>
                    <h3 className="SubTitle">{c.name}</h3>
                    <h3 className="SubTitle">Dificulty: {c.dificulty}</h3>
                    <h3 className="SubTitle">Duration: {c.duration} hs.</h3>
                    <h3 className="SubTitle">Season: {c.season}</h3>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="Observation">
              <p className="SubTitle">
                Observation: there are no activities planned for this country,
                please enter here.
                <Link className="SubTitle" to="/activities">
                  <button className="ButtonGoActivity">Create Activity</button>
                </Link>
              </p>
            </div>
          )}
        </div>
      ) : (
        <div className="loading">
          <h1> Loading... </h1>
        </div>
      )}
    </div>
  );
};

export default Detail;
