import React from "react";
import "./Paginated.css";

const Paginated = ({
  countriesPerPage,
  allCountries,
  currentPaginated,
  setCurrentPage,
}) => {
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(allCountries / countriesPerPage); i++) {
    pageNumbers.push(i);
  }

  const previusPage = () => {
    if (currentPaginated > 1) {
      setCurrentPage(currentPaginated - 1);
    }
  };

  const nextPage = () => {
    if (currentPaginated < pageNumbers.length) {
      setCurrentPage(currentPaginated + 1);
    }
  };

  const page = (n) => {
    setCurrentPage(n);
  };

  return (
    <nav className="Paginated">
      <button onClick={previusPage}>Prev</button>
      <button onClick={nextPage}>Next</button>
      <ul className="Paginated_List">
        {pageNumbers &&
          pageNumbers.map((number) => (
            <li key={number}>
              <button
                className={`Paginated_Number ${
                  number === currentPaginated ? "currentPage" : ""
                }`}
                onClick={() => currentPaginated(number)}
              >
                {number}
              </button>
            </li>
          ))}
      </ul>
    </nav>
  );
};

export default Paginated;
