import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Activity from "../Activity/Activity";
import NavBar from "../NavBar/NavBar";
import { getActivities } from "../../actions";
import "./ActivityList.css";

const ActivityList = () => {
  const dispatch = useDispatch();
  let [actState, setActState] = useState([]);

  useEffect(() => {
    dispatch(getActivities());
  }, [dispatch]);

  const activities = useSelector((state) => state.activities);
  console.log(activities);

  const handleSelect = (event) => {
    setActState(
      (actState = activities.find((act) => act.id === event.target.value))
    );
  };

  return (
    <div>
      <div>
        <NavBar />
      </div>
      {/* <div className="Activities_List">
        {activities.map((act) => {
          return (
            <div className="Activity_Item">
              <Activity
                name={act.name}
                duration={act.duration}
                dificulty={act.dificulty}
                season={act.season}
              />
            </div>
          );
        })}
      </div> */}
      <div>
        <select onChange={handleSelect}>
          <option disabled="true">Activities</option>
          {activities.map((act) => (
            <option value={act.id}>{act.name}</option>
          ))}
        </select>
        <div>
          <Activity
            name={actState.name}
            duration={actState.duration}
            dificulty={actState.dificulty}
            season={actState.season}
          />
        </div>
      </div>
    </div>
  );
};
export default ActivityList;
