import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { postActivity, getCountries } from "../../actions";
import { useDispatch, useSelector } from "react-redux";
import NavBar from "../NavBar/NavBar";

//todo MODIFICAR LA PROPIEDAD "DIFICULTY POR DIFFICULTY TODAAA LA APP"
const validate = (input) => {
  let errors = {};
  if (!input.name) {
    errors.name = "You must fill this field above";
  } else if (!input.duration) {
    errors.duration = "You must fill this field";
  } else if (!input.dificulty) {
    errors.dificulty = "You must choose the difficulty";
  } else if (!input.season) {
    errors.season = "You must choose the season";
  } else if (!input.countryId && input.countryId === []) {
    errors.countryId = "You must select a country";
  }
  return errors;
};

const ActivityCreate = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const countries = useSelector((state) => state.allCountries);
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    duration: 0,
    dificulty: "",
    season: "",
    countryId: [],
  });

  useEffect(() => {
    dispatch(getCountries());
  }, [dispatch]);

  const handleOnChange = (event) => {
    setInput({
      ...input,
      [event.target.name]: event.target.value,
    });
    setErrors(
      validate({
        ...input,
        [event.target.name]: event.target.value,
      })
    );
    console.log(errors);
  };

  const handleSelect = (event) => {
    setInput({
      ...input,
      countryId: [...input.countryId, event.target.value],
    });
    console.log(input);
  };

  const handleDelete = (country) => {
    setInput({
      ...input,
      countryId: input.countryId.filter((c) => c !== country),
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (
      input.name === "" ||
      input.duration === "" ||
      input.dificulty === "" ||
      input.season === "" ||
      input.countryId.length === 0
    )
      return alert("You must complete the fields");
    dispatch(postActivity(input));
    alert("Activity Created !!");
    setInput({
      name: "",
      duration: 0,
      dificulty: "",
      season: "",
      countryId: [],
    });
    navigate("/home");
  };

  return (
    <div>
      <div className="Nav_Bar">
        <NavBar />
      </div>
      <h1 className="TitleText">CREATE ACTIVITY</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={input.name}
            onChange={handleOnChange}
          />
        </div>
        {errors.name && <p className="Error">{errors.name}</p>}
        <div>
          <label>Duration:</label>
          <input
            type="number"
            name="duration"
            value={input.duration}
            onChange={handleOnChange}
          />
        </div>
        {errors.duration && <p className="Error">{errors.duration}</p>}
        <div>
          <label>Difficulty:</label>
          <input
            type="text"
            name="dificulty"
            value={input.dificulty}
            onChange={handleOnChange}
          />
        </div>
        {errors.dificulty && <p className="Error">{errors.dificulty}</p>}
        <div>
          <label>Season:</label>
          <input
            type="text"
            name="season"
            value={input.season}
            onChange={handleOnChange}
          />
          {errors.season && <p className="Error">{errors.season}</p>}
        </div>
        {errors.countryId && <p className="Error">{errors.countryId}</p>}
        <div>
          <select className="Form__Select" onChange={handleSelect}>
            <option className="op"> Countries </option>
            {countries.map((v) => (
              <option className="op" value={v.id}>
                {v.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          {input.countryId.map((country) => (
            <div>
              <input
                type="button"
                value="X"
                onClick={() => handleDelete(country)}
              />
              <p>{country}</p>
            </div>
          ))}
        </div>
        <div>
          <button type="submit">Create Activity</button>
        </div>
      </form>
    </div>
  );
};

export default ActivityCreate;
