import React from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getDetailCountry } from "../actions";

const Detail = () => {
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetailCountry(id));
  }, [dispatch]);
  const myCountry = useSelector((state) => state.detail);
  console.log(myCountry);

  return (
    <div>
      {myCountry.length && (
        <div>
          <h1>Country: {myCountry.name}</h1>
          <img src={myCountry.flag} alt={myCountry.name} />
          <div>
            <h1>Detail:</h1>
            <h2>ID: {myCountry.id}</h2>
            <h2>Capital: {myCountry.capital}</h2>
            <h2>Continent: {myCountry.continent}</h2>
            <h2>Subregion: {myCountry.subregion}</h2>
            <h2>Area: {myCountry.area}</h2>
            <h2>Population: {myCountry.population}</h2>
          </div>

          <div>
            {myCountry.Activities?.map((c) => {
              return (
                <div>
                  <Link to="/activities">
                    <h1>Activity</h1>
                  </Link>
                  <div>
                    <h3>{c.name}</h3>
                    <h3>{c.dificulty}</h3>
                    <h3>{c.duration}</h3>
                    <h3>{c.season}</h3>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default Detail;
