import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postActivity, getActivities, getCountries } from "../actions";
import { useDispatch, useSelector } from "react-redux";

const ActivityCreate = () => {
  const dispatch = useDispatch();
  const countries = useSelector((state) => state.allCountries);
  const [input, setInput] = useState({
    name: "",
    duration: 0,
    difficulty: "",
    season: "",
    countryId: [],
  });

  useEffect(() => {
    dispatch(getCountries());
  }, [dispatch]);

  const handleOnChange = (event) => {
    setInput({
      ...input,
      [event.target.name]: event.target.value,
    });
  };

  const handleSelect = (event) => {
    setInput({
      ...input,
      countryId: [...input.countryId, event.target.value],
    });
    console.log(input);
  };
  return (
    <div>
      <Link to="/home">
        <button>Volver</button>
      </Link>
      <h1>CREATE ACTIVITY</h1>
      <form>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={input.name}
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label>Duration:</label>
          <input
            type="number"
            name="duration"
            value={input.duration}
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label>Difficulty:</label>
          <input
            type="text"
            name="difficulty"
            value={input.difficulty}
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label>Season:</label>
          <input
            type="text"
            name="season"
            value={input.season}
            onChange={handleOnChange}
          />
        </div>

        <div className="Form__Input" onClick={handleSelect}>
          <select className="Form__Select">
            <option className="op"> Countries </option>
            {countries.map((v) => (
              <option className="op" value={v.id} onClick={handleSelect}>
                {v.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          {input.countryId.map((country) => (
            <div>
              <input type="button" value="X" />
              <p>{country}</p>
            </div>
          ))}
        </div>
      </form>
    </div>
  );
};

export default ActivityCreate;
