import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postActivity, getActivities } from "../actions";
import { useDispatch, useSelector } from "react-redux";

const ActivityCreate = () => {
  const dispatch = useDispatch();
  const countries = useSelector((state) => state.allCountries);

  const [input, setInput] = useState({
    name: "",
    difficulty: "",
    duration: 0,
    season: "",
    countryId: [],
  });
  useEffect(() => {
    dispatch(getActivities());
  }, [dispatch]);

  const handleSelect = (event) => {
    setInput({
      ...input,
      countryId: [...input.countryId, event.target.value],
    });
  };

  return (
    <div>
      <Link to="/home">
        <button>Volver</button>
      </Link>
      <h1>"Crea tu actividad"</h1>
      <form>
        <div>
          <label htmlFor="name">Name</label>
          <input type="text" value={input.name} name="name" />
        </div>
        <div>
          <label htmlFor="difficulty">Difficulty</label>
          <input type="text" value={input.difficulty} name="difficulty" />
        </div>
        <div>
          <label htmlFor="duration">Duration</label>
          <input type="text" value={input.duration} name="duration" />
        </div>
        <div>
          <label htmlFor="season">Season</label>
          <input type="text" value={input.season} name="season" />
        </div>

        <div>
          <select name="countries" onChange={handleSelect}>
            <option className="op">Countries</option>
            {countries.map((c) => (
              <option value={c.id}>{c.name}</option>
            ))}
          </select>
        </div>
        <div>
          {input.countryId.map((country) => (
            <div>
              <input className="Form__Button" type="button" value="X" />
              <p>{country}</p>
            </div>
          ))}
        </div>
        <div>
          <button className="Button_Submit" type="submit">
            Create Activity
          </button>
        </div>
      </form>
    </div>
  );
};

export default ActivityCreate;
