import React from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getCountries, getDetailCountry } from "../actions";

const Detail = () => {
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetailCountry(id));
    dispatch(getCountries());
  }, [dispatch]);

  const myCountry = useSelector((state) => state.detail);
  console.log(myCountry);

  return (
    <div>
      {myCountry ? (
        <div>
          <h1>Country: {myCountry.name}</h1>
          <img src={myCountry.flags} alt={myCountry.name} />
          <div>
            <h1>Detail:</h1>
            <h2>ID: {myCountry.id}</h2>
            <h2>Capital: {myCountry.capital}</h2>
            <h2>Continent: {myCountry.continent}</h2>
            {myCountry.subregion && <h2>Subregion: {myCountry.subregion}</h2>}
            {myCountry.area && <h2>Area: {myCountry.area} km2</h2>}
            <h2>Population: {myCountry.population}</h2>
          </div>

          {myCountry.Activities?.length ? (
            <div>
              <Link to="/activities">
                <h1>Activities</h1>
              </Link>
              {myCountry.Activities?.map((c) => {
                return (
                  <div>
                    <h3>{c.name}</h3>
                    <h3>Dificulty: {c.dificulty}</h3>
                    <h3>Duration: {c.duration} hs.</h3>
                    <h3>Season: {c.season}</h3>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="Observation">
              <p>
                Observation: there are no activities planned for this country,
                please enter here to create an activity.
              </p>
              <Link to="/activities">
                <button>Create Activity</button>
              </Link>
            </div>
          )}
        </div>
      ) : (
        <div className="loading">
          <h1> Loading... </h1>
        </div>
      )}
      <div>
        <Link to="/home">
          <button>Return</button>
        </Link>
      </div>
    </div>
  );
};

export default Detail;
